import React, { useState, useEffect } from "react";
import { getWeb3, getContract } from "./web3";

const App = () => {
  const [accounts, setAccounts] = useState([]);
  const [contract, setContract] = useState(null);
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    const init = async () => {
      try {
        const web3 = await getWeb3();
        const accounts = await web3.eth.getAccounts();
        const contract = await getContract(web3);

        setAccounts(accounts);
        setContract(contract);
      } catch (error) {
        console.error("Could not connect to contract or chain.");
      }
    };
    init();
  }, []);

  const handleTransfer = async () => {
    if (contract && recipient && amount) {
      try {
        await contract.methods.transferEther(recipient).send({
          from: accounts[0],

          value: window.web3.utils.toWei(amount, "ether"),
        });
        setMessage(`Transferred ${amount} ETH to ${recipient}`);
      } catch (error) {
        setMessage("Transaction failed.");
        console.error(error);
      }
    }
  };

  return (
    <div>
      <h1>Transfer Ether</h1>
      <p>From: {accounts[0]}</p>
      <input
        type="text"
        placeholder="Recipient Address"
        value={recipient}
        onChange={(e) => setRecipient(e.target.value)}
      />
      <input
        type="text"
        placeholder="Amount (ETH)"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
      />
      <button onClick={handleTransfer}>Send Ether</button>
      <p>{message}</p>
    </div>
  );
};

export default App;
