import React, { useState, useEffect } from "react";
import { getWeb3, getContract } from "./web3"; // Assurez-vous que cela pointe vers votre propre fichier web3.js

const App = () => {
  const [accounts, setAccounts] = useState([]);
  const [contract, setContract] = useState(null);
  const [selectedAccount, setSelectedAccount] = useState("");
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");
  const [contractBalance, setContractBalance] = useState(0);
  const [contractAddress, setContractAddress] = useState("");

  useEffect(() => {
    const init = async () => {
      try {
        const web3 = await getWeb3(); // Obtenir Web3
        const accounts = await web3.eth.getAccounts(); // Récupérer les comptes
        const contract = await getContract(web3); // Obtenir le contrat

        setAccounts(accounts); // Définir les comptes dans l'état
        setSelectedAccount(accounts[0]); // Sélectionner le premier compte par défaut
        setContract(contract); // Définir le contrat dans l'état
        setContractAddress(contract.options.address); // Obtenir l'adresse du contrat

        // Récupérer le solde du contrat
        const balance = await contract.methods.getBalance().call(); // Appeler getBalance
        setContractBalance(balance); // Définir le solde du contrat dans l'état
      } catch (error) {
        console.error("Error connecting to contract or blockchain", error);
      }
    };
    init();
  }, []);

  const handleTransfer = async () => {
    if (contract && recipient && amount) {
      try {
        await contract.methods.transfer(recipient).send({
          from: selectedAccount,
          value: window.web3.utils.toWei(amount, "ether"),
        });
        setMessage(`Transferred ${amount} ETH to ${recipient}`);

        // Mettre à jour le solde du contrat après le transfert
        const balance = await contract.methods.getBalance().call();
        setContractBalance(balance);
      } catch (error) {
        setMessage("Transaction failed.");
        console.error(error);
      }
    }
  };

  return (
    <div>
      <h1>ETH Transfer</h1>
      <label htmlFor="fromAccount">From:</label>
      <select
        id="fromAccount"
        value={selectedAccount}
        onChange={(e) => setSelectedAccount(e.target.value)}
      >
        {accounts.map((account, index) => (
          <option key={index} value={account}>
            {`Account ${index + 1}: ${account}`}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Recipient Address"
        value={recipient}
        onChange={(e) => setRecipient(e.target.value)}
      />
      <input
        type="text"
        placeholder="Amount (ETH)"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
      />
      <button onClick={handleTransfer}>Send Ether</button>
      <p>{message}</p>
      <h2>Contract Address: {contractAddress}</h2>
      <h2>Contract Balance: {window.web3.utils.fromWei(contractBalance, "ether")} ETH</h2>
    </div>
  );
};

export default App;
