import React, { useState } from 'react';
import Login from './components/connexion';
import Transfer from './components/Transfer';


const App = () => {
  const [selectedAccount, setSelectedAccount] = useState(null);

  const handleLogin = async () => {
    const account = window.ethereum.selectedAddress; // Récupérer l'adresse sélectionnée
    setSelectedAccount(account); // Mettre à jour l'état avec l'adresse du compte

  };

  return (
    <div>
      {!selectedAccount ? (
        <Login onLogin={handleLogin} />
      ) : (
        <>
          <h2>Bienvenue, {selectedAccount}</h2>
          {/* <Transfer selectedAccount={selectedAccount} /> */}
          <Transfer selectedAccount={selectedAccount} setSelectedAccount={setSelectedAccount} /> {/* Passer setSelectedAccount ici */}

        </>
      )}
    </div>
  );
};

export default App;
