import React, { useState, useEffect } from "react";
import { getWeb3, getContract } from "./web3";
import Login from './components/Login';


const App = () => {
  const [accounts, setAccounts] = useState([]);
  const [contract, setContract] = useState(null);
  const [selectedAccount, setSelectedAccount] = useState("");
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");
  const [contractBalance, setContractBalance] = useState(0);
  const [contractAddress, setContractAddress] = useState("");
  const [web3, setWeb3] = useState(null);

  useEffect(() => {
    const init = async () => {
      try {
        const web3Instance = await getWeb3();
        const accounts = await web3Instance.eth.getAccounts();
        console.log(accounts);
        const contractInstance = await getContract(web3Instance);

        setAccounts(accounts);
        setSelectedAccount(accounts[1]); // Sélectionner le premier compte par défaut
        setContract(contractInstance);
        setContractAddress(contractInstance.options.address);

        const balance = await contractInstance.methods.getBalance().call();
        setContractBalance(balance);
        setWeb3(web3Instance);
      } catch (error) {
        console.error("Error connecting to contract or blockchain", error);
      }
    };
    init();
  }, []);

  const handleTransfer = async () => {
    if (contract && recipient && amount) {
      try {
        await contract.methods.transfer(recipient).send({
          from: selectedAccount, // Utiliser le compte sélectionné
          value: web3.utils.toWei(amount, "ether"),
        });
        setMessage(`Transferred ${amount} ETH to ${recipient}`);

        const balance = await contract.methods.getBalance().call();
        setContractBalance(balance);
      } catch (error) {
        setMessage("Transaction failed.");
        console.error(error);
      }
    }
  };

  return (
    <div>
      <h1>ETH Transfer</h1>
      <label htmlFor="fromAccount">From:</label>
      <select
        id="fromAccount"
        value={selectedAccount}
        onChange={(e) => setSelectedAccount(e.target.value)} // Mise à jour du compte sélectionné
      >
        {accounts.map((account, index) => (
          <option key={index} value={account}>
            {`Account ${index + 1}: ${account}`} {/* Affichage de l'adresse */}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Recipient Address"
        value={recipient}
        onChange={(e) => setRecipient(e.target.value)}
      />
      <input
        type="text"
        placeholder="Amount (ETH)"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
      />
      <button onClick={handleTransfer}>Send Ether</button>
      <p>{message}</p>
      <h2>Contract Address: {contractAddress}</h2>
      <h2>Contract Balance: {web3 ? web3.utils.fromWei(contractBalance, "ether") : 1} ETH</h2>
    </div>
  );
};

export default App;
