// src/components/Transfer.js
import React, { useState, useEffect } from "react";
import Web3 from "web3";
import SimpleTransfer from "./contracts/EtherTransfer.json"; // Assurez-vous que le chemin est correct

const Transfer = () => {
  const [selectedAccount, setSelectedAccount] = useState(null);
  const [accounts, setAccounts] = useState([]);
  const [contract, setContract] = useState(null);
  const [accountBalance, setAccountBalance] = useState(0);
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");
  const [web3, setWeb3] = useState(null); // État pour web3
  const [isFormOpen, setFormOpen] = useState(false); // État pour le pop-up

  useEffect(() => {
    const init = async () => {
      try {
        const web3Instance = new Web3(window.ethereum);
        await window.ethereum.request({ method: "eth_requestAccounts" });

        // Récupérer tous les comptes de Ganache
        const accounts = await web3Instance.eth.getAccounts();
        setAccounts(accounts); // Stocker les comptes

        // Initialisation du contrat
        const networkId = await web3Instance.eth.net.getId();
        const deployedNetwork = SimpleTransfer.networks[networkId];
        const contractInstance = new web3Instance.eth.Contract(
          SimpleTransfer.abi,
          deployedNetwork && deployedNetwork.address
        );
        setContract(contractInstance); // Définir le contrat
        setWeb3(web3Instance); // Stocker l'instance de web3

        // Sélectionner le premier compte par défaut
        if (accounts.length > 0) {
          setSelectedAccount(accounts[0]);
          // Récupérer le solde du compte sélectionné
          const balance = await web3Instance.eth.getBalance(accounts[0]);
          setAccountBalance(web3Instance.utils.fromWei(balance, "ether")); // Convertir en ETH
        }
      } catch (error) {
        console.error("Error connecting to contract or blockchain", error);
      }
    };
    init();
  }, []);

  const handleTransfer = async () => {
    if (!recipient || !amount) {
      setMessage("Veuillez entrer une adresse et un montant valides.");
      return;
    }

    const value = Number(amount);
    if (value <= 0 || value > accountBalance) {
      setMessage("Le montant doit être supérieur à 0 et ne peut pas dépasser le solde.");
      return;
    }

    try {
      await contract.methods.transfer(recipient).send({
        from: selectedAccount, // Utiliser le compte sélectionné
        value: web3.utils.toWei(amount, "ether"), // Convertir le montant en Wei
      });
      setMessage(`Transfert de ${amount} ETH vers ${recipient} réussi.`);

      // Mettre à jour le solde du compte après le transfert
      const balance = await web3.eth.getBalance(selectedAccount); // Vérifier le nouveau solde
      setAccountBalance(web3.utils.fromWei(balance, "ether")); // Convertir en ETH
      closeForm(); // Fermer le pop-up après le transfert
    } catch (error) {
      setMessage("Échec du transfert : " + error.message);
      console.error(error);
    }
  };

  const openForm = () => setFormOpen(true);
  const closeForm = () => setFormOpen(false);

  return (
    <div className="container mt-5">
      <h1 className="text-center">Transfert de Cryptomonnaie</h1>
      {!selectedAccount ? (
        <button className="btn btn-primary" onClick={() => window.ethereum.request({ method: "eth_requestAccounts" })}>
          Connecter le Compte Principal
        </button>
      ) : (
        <>
          <h2>Compte : {selectedAccount}</h2>
          <h2>Solde : {accountBalance} ETH</h2>
          <button className="btn btn-success" onClick={openForm}>Faire un Transfert</button>
          <p>{message}</p>

          {/* Pop-up pour le formulaire de transfert */}
          {isFormOpen && (
            <div className="form-popup show card p-4 mt-3">
              <h3>Transférer des Ethers</h3>
              <label>Adresse du Destinataire:</label>
              <select className="form-select mb-3" value={recipient} onChange={(e) => setRecipient(e.target.value)}>
                <option value="">Sélectionnez une adresse</option>
                {accounts.map((account, index) => (
                  <option key={index} value={account}>
                    {account}
                  </option>
                ))}
              </select>

              <label>Montant (ETH):</label>
              <input
                type="number"
                className="form-control mb-3"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
              />

              <button className="btn btn-primary" onClick={handleTransfer}>Envoyer</button>
              <button className="btn btn-danger ms-2" onClick={closeForm}>Fermer</button>
            </div>
          )}

          {/* Liste des transactions simplifiée */}
          <h3 className="mt-5">Transactions du Compte Principal</h3>
          <ul className="list-group">
            {transactions.length > 0 ? (
              transactions.map((tx, index) => (
                <li
                  key={index}
                  className="list-group-item"
                  onClick={() => handleTransactionClick(tx)}
                  style={{ cursor: "pointer" }}
                >
                  <p><strong>Numéro de Transaction :</strong> {tx.hash.substring(0, 10)}...</p>
                  <p><strong>Montant :</strong> {tx.value && web3.utils.fromWei(tx.value, "ether")} ETH</p>
                </li>
              ))
            ) : (
              <p>Aucune transaction trouvée.</p>
            )}
          </ul>

          {/* Détails de la transaction sélectionnée */}
          {selectedTransaction && (
            <div className="card mt-4 p-3">
              <h4>Détails de la Transaction</h4>
              <p><strong>Hash:</strong> {selectedTransaction.hash}</p>
              <p><strong>De:</strong> {selectedTransaction.from}</p>
              <p><strong>À:</strong> {selectedTransaction.to}</p>
              <p><strong>Valeur:</strong> {selectedTransaction.value && web3.utils.fromWei(selectedTransaction.value, "ether")} ETH</p>
              <p><strong>Bloc:</strong> {selectedTransaction.blockNumber}</p>
              </div>
          )}
        </>
      )}


    </div>
  );
};

export default Transfer;
